function getTimeInCity(city) {
    const timeZones = {
        'new york': 'America/New_York',
        'london': 'Europe/London',
        'tokyo': 'Asia/Tokyo',
        'paris': 'Europe/Paris',
        'sydney': 'Australia/Sydney',
        'moscow': 'Europe/Moscow',
        'beijing': 'Asia/Shanghai',
        'dubai': 'Asia/Dubai',
        'los angeles': 'America/Los_Angeles',
        'chicago': 'America/Chicago'
    };
    
    if (timeZones[city.toLowerCase()]) {
        const options = { timeZone: timeZones[city.toLowerCase()], hour12: true, hour: '2-digit', minute: '2-digit' };
        const time = new Date().toLocaleTimeString('en-US', options);
        speak(`The current time in ${city} is ${time}`);
    } else {
        speak(`I don't have timezone information for ${city}`);
    }
}

